package palindrome

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestWithValidPalindrome(t *testing.T) {
	assert.True(t, isValidPalindrome("a"))
	assert.True(t, isValidPalindrome("aa"))
	assert.True(t, isValidPalindrome("aba"))
	assert.True(t, isValidPalindrome("abba"))
	assert.True(t, isValidPalindrome(" a bb a "))
	assert.True(t, isValidPalindrome("racecar"))
	assert.True(t, isValidPalindrome("kayak"))
}

func TestWithInvalidPalindrome(t *testing.T) {
	assert.False(t, isValidPalindrome("abca"))
	assert.False(t, isValidPalindrome("test"))
	assert.False(t, isValidPalindrome(" a bb a"))
	assert.False(t, isValidPalindrome("ABCDABCD"))
	assert.False(t, isValidPalindrome("Racecar"))
	assert.False(t, isValidPalindrome(""))
}
